<script setup lang="ts">
import { ref, watch } from 'vue';

const lastMouseEnterTimestamp = ref<number | null>(null);
const timerIsActive = ref<boolean>(false);
const isClicking = ref<boolean>(false);
const ANIMATION_DURATION = 1500;

function handleMouseEnter() {
  // Only save value if the last save is more than 1.5 seconds ago
  if (
    lastMouseEnterTimestamp.value &&
    new Date().getTime() - lastMouseEnterTimestamp.value < ANIMATION_DURATION + 500
  ) {
    return;
  }
  lastMouseEnterTimestamp.value = new Date().getTime();
}

function clickButton() {
  isClicking.value = true;
  setTimeout(() => {
    timerIsActive.value = !timerIsActive.value;
  }, 450);
  setTimeout(() => {
    isClicking.value = false;
  }, ANIMATION_DURATION);
}

watch(lastMouseEnterTimestamp, clickButton);
</script>

